import React from 'react'
import {Bar} from 'react-chartjs-2';

const Home =()=>{
  const state = {
    labels: ['January', 'February', 'March',
             'April', 'May'],
    datasets: [
      {
        label: 'Rainfall',
        backgroundColor: 'rgba(75,192,192,1)',
        borderColor: 'rgba(0,0,0,1)',
        borderWidth: 2,
        data: [65, 59, 80, 81, 56]
      }
    ]
  }
return(

    <div className="home">
      <h5>Metering Data</h5>
      <p>Youâ€™ve been provided with metering data in CSV format, metering_data.csv </p>
      <p>The file contains 2 electricity meters with cumulative readings for WH and VARH over time</p>
      
      <div>
        <Bar
          data={state}
          options={{
            title:{
              display:true,
              text:'Average Rainfall per month',
              fontSize:20
            },
            legend:{
              display:true,
              position:'right'
            }
          }}
        />
      </div>


    </div>

)

};
export default Home
