import React,{useState,useEffect} from 'react'
import {Link,useHistory} from 'react-router-dom'
import M from "materialize-css"
import {Line} from 'react-chartjs-2';
import DataGrid from 'react-data-grid';
import 'react-data-grid/dist/react-data-grid.css';
 

const Meter =()=>
{
  const history=useHistory();
  const[serial,setSerial] =useState("")
  const[meterreadingdata,setMeterreadingdata] =useState("")
  const[MeterGraph,setMeterGraph] =useState("")
  const[Metering,setMetering] =useState("")

  
  const columns = [
    
    { key: 'Serial', name: 'Serial' },
    { key: 'ReadingDateTimeUTC', name: 'ReadingDateTimeUTC' },
    { key: 'WH', name: 'WH' },
    { key: 'VARH', name: 'VARH' }
  ];
  
  const rows = [
    {
      "Serial": "",
      "ReadingDateTimeUTC": "",
      "WH": '',
      "VARH": ''
  }];

const MeteringData=[{
  "Serial": "",
  "ReadingDateTimeUTC": "",
  "WH": '',
  "VARH": ''
}]

  //setMetering(rows);
  //console.log("Metering Value : " + Metering);
  


  useEffect(()=>{
    console.log("useEffectData")
   
    if(meterreadingdata!=undefined)
    {

      var Mgraph=meterreadingdata.MeterGraph
      var Metering =meterreadingdata.Metering
      var MeterReading =meterreadingdata.MeterReading

            //setMeterGraph(Mgraph);
           
             //console.log("labelsReadingDatetime");
             //console.log(Mgraph.labelsReadingDatetime);
             //console.log(Mgraph);
             console.log(Metering);
           
             if(Array.isArray(Metering)){
              console.log("isArray");

              Metering.forEach(element =>  
                MeteringData.push(element)
               );

             }
            

             console.log(MeteringData);
            
             setMeterGraph(Mgraph)
             setMetering(Metering)
             
          
            

            

          //  setLineGraphData(GraphData);
 

    }
  
  },[meterreadingdata]);
 

  const renderResultRows=(data)=>{
    var self = this;
    return data.map(function(meter) 
    {
        return (
            <tr>
                <td data-title="Serial">{meter.Serial}</td>
                <td data-title="ReadingDateTimeUTC">{meter.ReadingDateTimeUTC}</td>
                <td data-title="WH">{meter.WH}</td>
                <td data-title="VARH">{meter.VARH}</td>
            </tr>
        );
    }.bind(this));

}

  const SearchSerial=()=>{
    var IsValid=true;
    
    var ToastMessage="";
    if(serial=="" || serial.length==0 || serial==undefined)
    {
      ToastMessage="";
      IsValid=false;
      ToastMessage= "serial is requied<br>"
    }
    if(serial.length>0 && (serial.length<11 || serial.length>11))
    {
      IsValid=false;
      ToastMessage+= "minlength   characters 11 required<br>"
    }
    if(IsValid==false)
    {
       var Msg =ToastMessage;
       ToastMessage="";
       return M.toast({html:Msg,classes:"#c62828 red darken-3"});
       
    }
    if(IsValid)
    {

        fetch("/search",{
            method:"POST",
            headers:{
              "Content-Type":"application/json"
            },
            body:JSON.stringify({serial})
           }).then(res=>res.json())
          .then(data=>{
            setMeterreadingdata("")
            if(data.error){
              M.toast({html: data.error,classes:"#c62828 red darken-3"})
              
            }
            else
            {
              //M.toast({html:data.message ,classes:"#00e676 green accent-3"})
               //history.push("/Signin");
             //console.log(data.MeterGraph);
              setMeterreadingdata(data)

            }
          });



    }//is valid if
 }//serach functions.






return(
<div>
<div className="search">
               <div className="card search input-field">
                 <h2>Search Meter Reading</h2>
                 <input 
                  type="text"
                  value={serial}
                  onChange={(e)=>setSerial(e.target.value)}
                  placeholder="Please Enter serial">
                 </input>
   
                <button className="btn waves-effect waves-light #1976d2 blue darken-1"
                onClick={()=>SearchSerial()}>
                    Search
                </button>
                  
               </div>
</div>

 <div className="showtalbe">
 
 <div id="no-more-tables">
            <table className="table table-hover table-bordered table-striped">
                <thead>
                    <tr>
                        <th>Serial</th>
                        <th>ReadingDateTime</th>
                        <th>WH</th>
                        <th>VARH</th>
                    </tr>
                </thead>
                <tbody>
                    {!MeteringData
                        ? this.renderResultRows(MeteringData)
                        : ''}
                </tbody>
            </table>
        </div>


     <DataGrid  columns={columns}  rows={MeteringData}/>

 </div>
 <div className="graph">
     <h5>Graph</h5>
     {/* <Line data={MeterGraph}></Line> */}
 </div>
</div>



 

)

};
export default Meter
