import React,{useState,useEffect} from 'react'
import {Link,useHistory} from 'react-router-dom'
import M from "materialize-css"
//import {Line} from 'react-chartjs-2';

const Meter =()=>
{
  const history=useHistory();
  const[serial,setSerial] =useState("")
  const[meterreadingdata,setMeterreadingdata] =useState("")
  
  useEffect(()=>{
    console.log("useEffectData")
   

    var Mgraph=meterreadingdata.MeterGraph
    if(Mgraph!=undefined)
    {
           
             console.log("labelsReadingDatetime");
             console.log(Mgraph.labelsReadingDatetime);
             console.log(Mgraph.labelWH);
             console.log(Mgraph.dataWH);
             console.log(Mgraph.lableVARH);
             console.log(Mgraph.dataVARH);

             const data = {
              labels: ['"21/03/2020 10:30 PM"', '"21/03/2020 10:30 PM"', '"21/03/2020 10:30 PM"', '"21/03/2020 10:30 PM"', '"21/03/2020 10:30 PM"', '"21/03/2020 10:30 PM"', '"21/03/2020 10:30 PM"'],
              datasets: [
                {
                  label:Mgraph.labelWH,
                  fill: false,
                  lineTension: 0.1,
                  backgroundColor: 'rgba(75,192,192,0.4)',
                  borderColor: 'rgba(75,192,192,1)',
                  borderCapStyle: 'butt',
                  borderDash: [],
                  borderDashOffset: 0.0,
                  borderJoinStyle: 'miter',
                  pointBorderColor: 'rgba(75,192,192,1)',
                  pointBackgroundColor: '#fff',
                  pointBorderWidth: 1,
                  pointHoverRadius: 5,
                  pointHoverBackgroundColor: 'rgba(75,192,192,1)',
                  pointHoverBorderColor: 'rgba(220,220,220,1)',
                  pointHoverBorderWidth: 2,
                  pointRadius: 1,
                  pointHitRadius: 10,
                  data: Mgraph.dataWH//[65, 59, 80, 81, 56, 55, 40]
                },
                {
                  label:Mgraph.labelWH,
                  fill: false,
                  lineTension: 0.1,
                  backgroundColor: 'rgba(75,192,192,0.4)',
                  borderColor: 'rgba(75,192,192,1)',
                  borderCapStyle: 'butt',
                  borderDash: [],
                  borderDashOffset: 0.0,
                  borderJoinStyle: 'miter',
                  pointBorderColor: 'rgba(75,192,192,1)',
                  pointBackgroundColor: '#fff',
                  pointBorderWidth: 1,
                  pointHoverRadius: 5,
                  pointHoverBackgroundColor: 'rgba(75,192,192,1)',
                  pointHoverBorderColor: 'rgba(220,220,220,1)',
                  pointHoverBorderWidth: 2,
                  pointRadius: 1,
                  pointHitRadius: 10,
                  data: Mgraph.dataWH//[65, 59, 80, 81, 56, 55, 40]
                }
              ]
            };
            
 

    }
    //console.log(Mgraph);
    // console.log(Mgraph.labelsReadingDatetime);
    // console.log(Mgraph.labelWH);
    // console.log(Mgraph.dataWH);
    // console.log(Mgraph.lableVARH);
    // console.log(Mgraph.dataVARH);

  

  },[meterreadingdata]);

  const SearchSerial=()=>{
    var IsValid=true;
    
    var ToastMessage="";
    if(serial=="" || serial.length==0 || serial==undefined)
    {
      ToastMessage="";
      IsValid=false;
      ToastMessage= "serial is requied<br>"
    }
    if(serial.length>0 && (serial.length<11 || serial.length>11))
    {
      IsValid=false;
      ToastMessage+= "minlength   characters 11 required<br>"
    }
    if(IsValid==false)
    {
       var Msg =ToastMessage;
       ToastMessage="";
       return M.toast({html:Msg,classes:"#c62828 red darken-3"});
       
    }
    if(IsValid)
    {

        fetch("/search",{
            method:"POST",
            headers:{
              "Content-Type":"application/json"
            },
            body:JSON.stringify({serial})
           }).then(res=>res.json())
          .then(data=>{
            setMeterreadingdata("")
            if(data.error){
              M.toast({html: data.error,classes:"#c62828 red darken-3"})
              
            }
            else
            {
              //M.toast({html:data.message ,classes:"#00e676 green accent-3"})
               //history.push("/Signin");
             //console.log(data.MeterGraph);
              setMeterreadingdata(data)

            }
          });



    }//is valid if
 }//serach functions.

return(
<div>
<div className="search">
               <div className="card search input-field">
                 <h2>Search Meter Reading</h2>
                 <input 
                  type="text"
                  value={serial}
                  onChange={(e)=>setSerial(e.target.value)}
                  placeholder="Please Enter serial">
                 </input>
   
                <button className="btn waves-effect waves-light #1976d2 blue darken-1"
                onClick={()=>SearchSerial()}>
                    Search
                </button>
                  
               </div>
</div>

 <div className="graph">
     <h5>Graph</h5>
     
     {/* <Line data={data} /> */}
 </div>
</div>



 

)

};
export default Meter
