import React from 'react'
import {Bar} from 'react-chartjs-2';
import {Line} from 'react-chartjs-2';
const Home =()=>{

  // const data = {
  //   labels = ["21/03/2020 10:30 PM", "bbb", "ccc"],
  //   datasets = [
  //    { label: "WH", backgroundColor: "rgba(75,192,192,1)",borderColor: 'rgba(0,0,0,1)', data: [27180198]},
  //     { label: "VARH", backgroundColor: "rgba(75,192,192,1)",borderColor: 'rgba(0,0,2,2)', data: [6232597]},
  //   ]
  //  }


  const state = {
    labels: ['January', 'February', 'March',
             'April', 'May'],
    datasets: [
      {
        label: 'Rainfall',
        backgroundColor: 'rgba(75,192,192,1)',
        borderColor: 'rgba(0,0,0,1)',
        borderWidth: 2,
        data: [65, 59, 80, 81, 56]
      }
    ]
  }
  const data = {
    labels: ['January', 'February', 'March', 'April', 'May', 'June', 'July'],
    datasets: [
      {
        label: 'My First dataset',
        fill: false,
        lineTension: 0.1,
        backgroundColor: 'rgba(75,192,192,0.4)',
        borderColor: 'rgba(75,192,192,1)',
        borderCapStyle: 'butt',
        borderDash: [],
        borderDashOffset: 0.0,
        borderJoinStyle: 'miter',
        pointBorderColor: 'rgba(75,192,192,1)',
        pointBackgroundColor: '#fff',
        pointBorderWidth: 1,
        pointHoverRadius: 5,
        pointHoverBackgroundColor: 'rgba(75,192,192,1)',
        pointHoverBorderColor: 'rgba(220,220,220,1)',
        pointHoverBorderWidth: 2,
        pointRadius: 1,
        pointHitRadius: 10,
        data: [65, 59, 80, 81, 56, 55, 40]
      }
    ]
  };
  


  
return(

    <div className="home">

      
      <h5>Metering Data</h5>
      <p>Youâ€™ve been provided with metering data in CSV format, metering_data.csv </p>
      <p>The file contains 2 electricity meters with cumulative readings for WH and VARH over time</p>
      
      <div>
        <Bar
          data={state}
          options={{
            title:{
              display:true,
              text:'Average Rainfall per month',
              fontSize:20
            },
            legend:{
              display:true,
              position:'right'
            }
          }}
        />
      </div>

      <Line data={data} />


    </div>

)

};
export default Home
